import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AddBookForm = ({ onAddBook }) => {
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    status: '',
    notes: ''
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
  e.preventDefault();
  axios
    .post(`${process.env.REACT_APP_API_URL}/books`, formData, { withCredentials: true }) // Correct placement of formData and withCredentials
    .then((res) => {
      setMessage('Book added successfully!');
      onAddBook(res.data); // Notify parent about the new book
      setFormData({ title: '', author: '', status: '', notes: '' }); // Reset form
    })
    .catch((err) => {
      console.error('Error adding book:', err.response?.data || err.message); 
      setMessage(err.response?.data?.message || 'Error adding book');
    });
};

  return (
    <form
  onSubmit={handleSubmit}
  className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4 space-y-4"
>
  {/* Horizontal input group */}
  <div className="flex flex-col md:flex-row md:items-center md:space-x-4 space-y-4 md:space-y-0">
    <input
      type="text"
      placeholder="Title"
      value={formData.title}
      onChange={e => setFormData({ ...formData, title: e.target.value })}
      className="flex-1 px-3 py-2 border border-gray-300 rounded"
    />
    <input
      type="text"
      placeholder="Author"
      value={formData.author}
      onChange={e => setFormData({ ...formData, author: e.target.value })}
      className="flex-1 px-3 py-2 border border-gray-300 rounded"
    />
    <input
      type="text"
      placeholder="Status"
      value={formData.status}
      onChange={e => setFormData({ ...formData, status: e.target.value })}
      className="flex-1 px-3 py-2 border border-gray-300 rounded"
    />
  </div>

  {/* Notes and button stacked below */}
  <textarea
    placeholder="Notes"
    value={formData.notes}
    onChange={e => setFormData({ ...formData, notes: e.target.value })}
    className="w-full px-3 py-2 border border-gray-300 rounded"
  ></textarea>

  <button
    type="submit"
    className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded"
  >
    Add Book
  </button>
</form>
  );
};

export default AddBookForm;
