import React, { useState } from 'react';
import axios from 'axios';

const AddBookForm = () => {
    const[formData, setFormData] = useState({
        title: '',
        author: '',
        status: '',
        notes: ''
    });

const handleSubmit = (e) => {
e.preventDefault();
axios.post('http://localhost:6000/books', formData)
    .then((response) => {
        console.log('Book added:', response.data);
        // Book added
    })
    .catch((error) => {
        console.error('Error adding book:', error);
        // Error handling
    });
}

return(
    <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Title" onChange={e => setFormData({ ...formData, title: e.target.value })} />
      <input type="text" placeholder="Author" onChange={e => setFormData({ ...formData, author: e.target.value })} />
      <input type="text" placeholder="Status" onChange={e => setFormData({ ...formData, status: e.target.value })} />
      <textarea placeholder="Notes" onChange={e => setFormData({ ...formData, notes: e.target.value })}></textarea>
      <button type="submit">Add Book</button>
    </form>

);
};

export default AddBookForm;
